          "identifier": "",
          "guid": "0000-0009-0002e",
          "xml": "<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"no\"?>\r\n<qti-assessment-item \r\n  xmlns=\"http:\/\/www.imsglobal.org\/xsd\/imsqtiasi_v3p0\" \r\n  xmlns:xsi=\"http:\/\/www.w3.org\/2001\/XMLSchema-instance\" \r\n  xsi:schemaLocation=\" http:\/\/www.imsglobal.org\/xsd\/imsqtiasi_v3p0 https:\/\/purl.imsglobal.org\/spec\/qti\/v3p0\/schema\/xsd\/imsqti_asiv3p0_v1p0.xsd \" \r\n  identifier=\"mathxl-08b-nextgen\" title=\"mathxl-08b-nextgen\" adaptive=\"true\" time-dependent=\"false\">\r\n\r\n<qti-response-declaration base-type=\"identifier\" cardinality=\"single\" identifier=\"RESPONSE2\">\r\n  <qti-correct-response>\r\n    <qti-value>A<\/qti-value>\r\n  <\/qti-correct-response>\r\n<\/qti-response-declaration>\r\n\r\n<qti-response-declaration base-type=\"string\" cardinality=\"single\" identifier=\"RESPONSE4\"\/>\r\n<qti-response-declaration base-type=\"float\" cardinality=\"single\" identifier=\"RESPONSE4_FLOAT\"\/>\r\n\r\n<qti-response-declaration base-type=\"string\" cardinality=\"single\" identifier=\"RESPONSE7\"\/>\r\n<qti-response-declaration base-type=\"float\" cardinality=\"single\" identifier=\"RESPONSE7_FLOAT\"\/>\r\n\r\n<qti-response-declaration base-type=\"boolean\" cardinality=\"single\" identifier=\"EndAttempt\" \/>\r\n  \r\n<qti-outcome-declaration base-type=\"float\" cardinality=\"single\" identifier=\"SCORE\" normal-maximum=\"1\" normal-minimum=\"0\">\r\n  <qti-default-value>\r\n    <qti-value>0<\/qti-value>\r\n  <\/qti-default-value>\r\n<\/qti-outcome-declaration>\r\n\r\n<!-- Define a STEP variable to keep track of our current step -->  \r\n<qti-outcome-declaration base-type=\"integer\" cardinality=\"single\" identifier=\"STEP\">\r\n  <qti-default-value>\r\n    <qti-value>1<\/qti-value>\r\n  <\/qti-default-value>\r\n<\/qti-outcome-declaration>\r\n  \r\n<!-- Define a feedback variable; its cardinality is \"multiple\" so that it can contain the identifiers of several \r\n     feedback elements, and in this case it is initialized to the identifier, step1, of the first part of this \r\n     adaptive question -->            \r\n<qti-outcome-declaration base-type=\"identifier\" cardinality=\"multiple\" identifier=\"BODY\">\r\n  <qti-default-value>\r\n    <qti-value>step1<\/qti-value>\r\n  <\/qti-default-value>\r\n<\/qti-outcome-declaration>\r\n\r\n<qti-outcome-declaration base-type=\"identifier\" cardinality=\"single\" identifier=\"FEEDBACK2\" \/>\r\n<qti-outcome-declaration base-type=\"identifier\" cardinality=\"single\" identifier=\"FEEDBACK4\" \/>\r\n<qti-outcome-declaration base-type=\"identifier\" cardinality=\"single\" identifier=\"FEEDBACK7\" \/>\r\n<!-- Define a variable used to display the student's response in feedback -->\r\n<qti-outcome-declaration base-type=\"string\" cardinality=\"single\" identifier=\"FEEDBACK7_RESPONSE\" \/>\r\n\r\n<qti-template-declaration identifier=\"SALESPRICE\" cardinality=\"single\" base-type=\"float\" math-variable=\"false\" param-variable=\"false\"\/>\r\n<qti-template-declaration identifier=\"TAX_PERCENT\" cardinality=\"single\" base-type=\"float\" math-variable=\"true\" param-variable=\"false\"\/>\r\n<qti-template-declaration identifier=\"SALESTAX\" cardinality=\"single\" base-type=\"float\" math-variable=\"false\" param-variable=\"false\"\/>\r\n<qti-template-declaration identifier=\"TOTAL\" cardinality=\"single\" base-type=\"float\" math-variable=\"false\" param-variable=\"false\"\/>\r\n  \r\n<qti-template-processing>\r\n    <qti-set-template-value identifier=\"SALESPRICE\">\r\n      <qti-integer-to-float>\r\n        <qti-random-integer min=\"2\" max=\"99\" step=\"1\"\/>\r\n      <\/qti-integer-to-float>\r\n    <\/qti-set-template-value>\r\n    <qti-set-template-value identifier=\"TAX_PERCENT\">\r\n      <qti-divide>\r\n        <qti-random-integer min=\"300\" max=\"700\" step=\"25\"\/>\r\n        <qti-base-value base-type=\"float\">100<\/qti-base-value>\r\n      <\/qti-divide>\r\n    <\/qti-set-template-value>\r\n    <qti-set-template-value identifier=\"SALESTAX\">\r\n      <qti-divide>\r\n        <qti-variable identifier=\"TAX_PERCENT\"\/>\r\n        <qti-base-value base-type=\"float\">100<\/qti-base-value>\r\n      <\/qti-divide>\r\n    <\/qti-set-template-value>\r\n    <qti-set-template-value identifier=\"TOTAL\">\r\n      <qti-round-to rounding-mode=\"decimalPlaces\" figures=\"2\">\r\n        <qti-product>\r\n          <qti-variable identifier=\"SALESTAX\"\/>\r\n          <qti-variable identifier=\"SALESPRICE\"\/>\r\n        <\/qti-product>\r\n      <\/qti-round-to>\r\n    <\/qti-set-template-value>\r\n    <qti-set-correct-response identifier=\"RESPONSE4_FLOAT\">\r\n      <qti-variable identifier=\"SALESPRICE\"\/>\r\n    <\/qti-set-correct-response>\r\n    <qti-set-correct-response identifier=\"RESPONSE7_FLOAT\">\r\n      <qti-variable identifier=\"TOTAL\"\/>\r\n    <\/qti-set-correct-response>\r\n<\/qti-template-processing>\r\n\r\n<qti-item-body>\r\n      <div class=\"qti3-player-item-card-bordered-rounded qti3-player-item-card-raised-rounded\">\r\n        <div class=\"qti3-player-item-card-body-scrollable qti-padding-2\">\r\n      \r\n          <p>Find the sales tax.<\/p>\r\n          <table class=\"table table-bordered\" style=\"width:400px;\">\r\n            <thead>\r\n              <tr>\r\n\t        <th class=\"qti-align-center\" colspan=\"3\">Sales Tax<\/th>\r\n\t      <\/tr>\r\n\t      <tr>\r\n\t        <th scope=\"col\" class=\"qti-align-center\">Selling Price<\/th>\r\n\t        <th scope=\"col\" class=\"qti-align-center\">Rate of Sales Tax<\/th>\r\n\t        <th scope=\"col\" class=\"qti-align-center\">Sales Tax<\/th>\r\n              <\/tr>\r\n            <\/thead>\r\n            <tbody>\r\n              <tr>\r\n                <td class=\"qti-align-center\">$<qti-printed-variable identifier=\"SALESPRICE\" format=\"%.2f\" \/><\/td>\r\n                <td class=\"qti-align-center\"><qti-printed-variable identifier=\"TAX_PERCENT\"\/>%<\/td>\r\n                <td class=\"qti-align-center\">?<\/td>\r\n              <\/tr>\r\n            <\/tbody>\r\n          <\/table>\r\n          \r\n          <hr \/>\r\n        \r\n          <qti-feedback-block id=\"feedbackBlock1\" identifier=\"step1\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <p>Use the percent equation, part = percent \u00D7 whole, to find the sales tax.<\/p>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n\r\n          <qti-feedback-block id=\"feedbackBlock2\" identifier=\"step2\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <p>Which of these represents the dollar amount of the sales tax?<\/p>\r\n              <qti-choice-interaction class=\"qti-labels-none\" max-choices=\"1\" response-identifier=\"RESPONSE2\" shuffle=\"true\">\r\n                <qti-simple-choice identifier=\"A\">Sales tax rate times selling price<\/qti-simple-choice>\r\n                <qti-simple-choice identifier=\"B\">Sales tax rate divided by selling price<\/qti-simple-choice>\r\n                <qti-simple-choice identifier=\"C\">Sales tax rate plus selling price<\/qti-simple-choice>\r\n                <qti-simple-choice identifier=\"D\">Sales tax rate minus selling price<\/qti-simple-choice>\r\n              <\/qti-choice-interaction>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n\r\n          <qti-feedback-block id=\"feedbackBlock3\" identifier=\"step3\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <p>Substitute into the percent equation.<\/p>\r\n              <table class=\"indent-1\">\r\n                <tbody>\r\n                  <tr>\r\n                    <td class=\"qti-align-right\" style=\"width:100px;\">sales tax<\/td>\r\n                    <td>= sales tax rate \u00D7 selling price<\/td>\r\n                  <\/tr>\r\n                <\/tbody>\r\n              <\/table>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n\r\n          <qti-feedback-block id=\"feedbackBlock4\" identifier=\"step4\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <table class=\"indent-1\">\r\n                <tbody>\r\n                  <tr>\r\n                    <td class=\"qti-align-right\" style=\"width:100px;\"><span> <\/span><\/td>\r\n                    <td>= <qti-printed-variable identifier=\"TAX_PERCENT\"\/>% \u00D7 <qti-text-entry-interaction class=\"qti-input-width-6\" pattern-mask=\"([0-9.\\-]{0,8})\" response-identifier=\"RESPONSE4\"\/><\/td>\r\n                  <\/tr>\r\n                <\/tbody>\r\n              <\/table>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n          \r\n          <qti-feedback-block id=\"feedbackBlock5\" identifier=\"step5\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <p style=\"margin-top:1rem;\">\r\n                The fraction form of the sales tax rate is \r\n                <math xmlns=\"http:\/\/www.w3.org\/1998\/Math\/MathML\" display=\"inline\" overflow=\"scroll\">\r\n                  <mrow>\r\n                    <mfrac><mrow><mi>TAX_PERCENT<\/mi><\/mrow><mrow><mi>100<\/mi><\/mrow><\/mfrac>\r\n                  <\/mrow>\r\n                <\/math>.\r\n              <\/p>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n\r\n          <qti-feedback-block id=\"feedbackBlock6\" identifier=\"step6\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n              <p>Solve the percent equation.<\/p>\r\n              <table class=\"indent-1\">\r\n                <tbody>\r\n                  <tr>\r\n                    <td class=\"qti-align-right qti-valign-middle\" style=\"width:100px;\">sales tax<\/td>\r\n                    <td class=\"qti-valign-middle\">= <math xmlns=\"http:\/\/www.w3.org\/1998\/Math\/MathML\" display=\"inline\" overflow=\"scroll\">\r\n                  <mrow>\r\n                    <mfrac><mrow><mi>TAX_PERCENT<\/mi><\/mrow><mrow><mi>100<\/mi><\/mrow><\/mfrac>\r\n                  <\/mrow>\r\n                <\/math> \u00D7 <qti-printed-variable identifier=\"SALESPRICE\" format=\"%.2f\" \/><\/td><\/tr><\/tbody><\/table>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n          \r\n          <qti-feedback-block id=\"feedbackBlock7\" identifier=\"step7\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\r\n\t\t<table class=\"indent-1\" style=\"margin-bottom:1rem\"><tbody><tr><td style=\"width:100px\"><span> <\/span><\/td><td>= <qti-text-entry-interaction class=\"qti-input-width-6\" pattern-mask=\"([0-9.\\-]{0,8})\" response-identifier=\"RESPONSE7\"\/> <span class=\"text-muted\">(Round to two decimal places as needed)<\/span><\/td><\/tr><\/tbody><\/table>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n\r\n          <qti-feedback-block id=\"feedbackBlock8\" identifier=\"step8\" outcome-identifier=\"BODY\" show-hide=\"show\">\r\n            <qti-content-body>\t\t\t\t\r\n\t\t<p style=\"margin-top:1rem;margin-bottom:3rem;\">Thus, the sales tax is $<qti-printed-variable identifier=\"TOTAL\" format=\"%.2f\" \/>.<\/p>\r\n            <\/qti-content-body>\r\n          <\/qti-feedback-block>\r\n          \r\n      <\/div> <!-- \/card-body -->\r\n    \r\n      <div class=\"qti3-player-item-card-footer qti-height-14 qti-padding-2\">\r\n        <qti-end-attempt-interaction title=\"Continue\" class=\"endattempt-controller-bar\" data-steps=\"8\" data-controller-type=\"showexample\" data-hastemplates=\"true\" response-identifier=\"EndAttempt\"\/>\r\n      <\/div> <!-- \/card-footer -->\r\n      \r\n    <\/div> <!-- \/card --> \r\n<\/qti-item-body>\r\n\r\n<qti-response-processing>\r\n\r\n  <qti-response-condition>\r\n    <qti-response-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">1<\/qti-base-value>\r\n      <\/qti-equal>\r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">2<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step2<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-if>\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">2<\/qti-base-value>\r\n      <\/qti-equal>\r\n      \r\n      <qti-response-condition>\r\n        <qti-response-if>\r\n          <qti-match>\r\n            <qti-variable identifier=\"RESPONSE2\"\/>\r\n            <qti-correct identifier=\"RESPONSE2\"\/>\r\n          <\/qti-match>\r\n          <qti-set-outcome-value identifier=\"FEEDBACK2\">\r\n            <qti-base-value base-type=\"identifier\">correct<\/qti-base-value>\r\n          <\/qti-set-outcome-value>\r\n        <\/qti-response-if>\r\n        <qti-response-else>\r\n          <qti-set-outcome-value identifier=\"FEEDBACK2\">\r\n            <qti-base-value base-type=\"identifier\">incorrect<\/qti-base-value>\r\n          <\/qti-set-outcome-value>\r\n        <\/qti-response-else>\r\n      <\/qti-response-condition>\r\n      \r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">3<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step3<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n      \r\n    <\/qti-response-else-if>\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">3<\/qti-base-value>\r\n      <\/qti-equal>\r\n      \r\n      <!-- Turn off Step 2 Feedback -->\r\n      <qti-set-outcome-value identifier=\"FEEDBACK2\">\r\n      \t<qti-base-value base-type=\"identifier\">step3<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      \r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">4<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step4<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else-if>\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">4<\/qti-base-value>\r\n      <\/qti-equal>\r\n      \r\n      <qti-response-condition>\r\n        <qti-response-if>\r\n          <!-- Check numeric equivalence -->\r\n          <qti-equal tolerance-mode=\"exact\">\r\n            <!-- The value of RESPONSE4 is compared with the correct value identified in the RESPONSE4_FLOAT declaration-->\r\n            <qti-custom-operator class=\"math.stringToFloat\">\r\n              <qti-variable identifier=\"RESPONSE4\"\/>\r\n            <\/qti-custom-operator>\r\n            <qti-correct identifier=\"RESPONSE4_FLOAT\"\/>\r\n          <\/qti-equal>\r\n          <qti-set-outcome-value identifier=\"FEEDBACK4\">\r\n            <qti-base-value base-type=\"identifier\">correct<\/qti-base-value>\r\n          <\/qti-set-outcome-value>\r\n        <\/qti-response-if>\r\n        <qti-response-else>\r\n          <qti-set-outcome-value identifier=\"FEEDBACK4\">\r\n            <qti-base-value base-type=\"identifier\">incorrect<\/qti-base-value>\r\n          <\/qti-set-outcome-value>\r\n        <\/qti-response-else>\r\n      <\/qti-response-condition>\r\n      \r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">5<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step5<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else-if>\r\n\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">5<\/qti-base-value>\r\n      <\/qti-equal>\r\n      \r\n      <!-- Turn off Step 4 Feedback -->\r\n      <qti-set-outcome-value identifier=\"FEEDBACK4\">\r\n        <qti-base-value base-type=\"identifier\">step5<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      \r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">6<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step6<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else-if>\r\n\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">6<\/qti-base-value>\r\n      <\/qti-equal>\r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">7<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step7<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else-if>\r\n\r\n    <qti-response-else-if>\r\n      <qti-equal>\r\n        <qti-variable identifier=\"STEP\"\/>\r\n        <qti-base-value base-type=\"integer\">7<\/qti-base-value>\r\n      <\/qti-equal>\r\n      \r\n      <!-- Save off the response so we can echo it back to the student in feedback - if needed -->\r\n      <qti-set-outcome-value identifier=\"FEEDBACK7_RESPONSE\">\r\n        <qti-variable identifier=\"RESPONSE7\"\/>\r\n      <\/qti-set-outcome-value>\r\n      \r\n      <qti-response-condition>\r\n        <qti-response-if>\r\n          <!-- Check numeric equivalence -->\r\n          <qti-equal tolerance-mode=\"exact\">\r\n            <!-- The value of RESPONSE7 is compared with the correct value identified in the RESPONSE7_FLOAT declaration-->\r\n            <qti-custom-operator class=\"math.stringToFloat\">\r\n              <qti-variable identifier=\"RESPONSE7\"\/>\r\n            <\/qti-custom-operator>\r\n            <qti-correct identifier=\"RESPONSE7_FLOAT\"\/>\r\n          <\/qti-equal>\r\n          \r\n          <!-- Response is numerically equivalent to the correct response.  Now check form. -->\r\n          <qti-response-condition>\r\n            <qti-response-if>\r\n              <qti-pattern-match pattern=\"[0-9]*\\.[0-9]{2}\">\r\n                <qti-variable identifier=\"RESPONSE7\"\/>\r\n              <\/qti-pattern-match>\r\n              <qti-set-outcome-value identifier=\"SCORE\">\r\n\t        <qti-base-value base-type=\"float\">1<\/qti-base-value>\r\n\t      <\/qti-set-outcome-value>\r\n\t      <qti-set-outcome-value identifier=\"FEEDBACK7\">\r\n\t        <qti-base-value base-type=\"identifier\">correct<\/qti-base-value>\r\n              <\/qti-set-outcome-value>\r\n            <\/qti-response-if>\r\n            <qti-response-else>\r\n              <qti-set-outcome-value identifier=\"FEEDBACK7\">\r\n\t        <qti-base-value base-type=\"identifier\">wrongformat<\/qti-base-value>\r\n              <\/qti-set-outcome-value>\r\n            <\/qti-response-else>\r\n          <\/qti-response-condition>\r\n        <\/qti-response-if>\r\n        <qti-response-else>\r\n        <!--Depending on whether the input matches the correct answer or not, FEEDBACK  is given the value of the identifier of the appropriate feedback message-->\r\n          <qti-set-outcome-value identifier=\"FEEDBACK7\">\r\n            <qti-base-value base-type=\"identifier\">incorrect<\/qti-base-value>\r\n          <\/qti-set-outcome-value>\r\n        <\/qti-response-else>\r\n      <\/qti-response-condition>\r\n      \r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">8<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <qti-set-outcome-value identifier=\"BODY\">\r\n        <qti-multiple>\r\n          <qti-variable identifier=\"BODY\"\/>\r\n          <qti-base-value base-type=\"identifier\">step8<\/qti-base-value>\r\n        <\/qti-multiple>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else-if>\r\n    \r\n    <qti-response-else>\r\n      <qti-set-outcome-value identifier=\"STEP\">\r\n        <qti-base-value base-type=\"integer\">8<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n      <!-- completionStatus must be specifically set to completed in adaptive questions -->\r\n      <qti-set-outcome-value identifier=\"completionStatus\">\r\n        <qti-base-value base-type=\"identifier\">completed<\/qti-base-value>\r\n      <\/qti-set-outcome-value>\r\n    <\/qti-response-else>\r\n  <\/qti-response-condition>\r\n<\/qti-response-processing>\r\n\r\n<qti-modal-feedback outcome-identifier=\"FEEDBACK2\" identifier=\"correct\" show-hide=\"show\">\r\n  <qti-content-body>Well done!<\/qti-content-body>\r\n<\/qti-modal-feedback>\r\n<qti-modal-feedback outcome-identifier=\"FEEDBACK2\" identifier=\"incorrect\" show-hide=\"show\">\r\n  <qti-content-body>\r\n    <p>Sorry, your answer is n