      <mtd columnalign=\"right\"\/>\r\n                                    <mtd columnalign=\"center\">\r\n                                        <mo>=<\/mo>\r\n                                    <\/mtd>\r\n                                    <mtd columnalign=\"left\">\r\n                                        <mi>fR<\/mi>\r\n                                    <\/mtd>\r\n                                <\/mtr>\r\n                            <\/mtable>\r\n                            <annotation encoding=\"SnuggleTeX\">\\begin{eqnarray*}e^\\qv{iA}\r\n                                &amp;=&amp; \\exp(\\qv{iA})\\\\ &amp;=&amp;\r\n                                \\qv{fR}\\end{eqnarray*}<\/annotation>\r\n                        <\/semantics>\r\n                    <\/math>\r\n                <\/div>\r\n            <\/qti-content-body><\/qti-feedback-block>\r\n            <!-- Explain why the score is zero (after viewing solution) -->\r\n            <qti-feedback-block id=\"feedbackBlock4\" identifier=\"SEEN-SOLUTION\" outcome-identifier=\"FEEDBACK\" show-hide=\"show\"><qti-content-body>\r\n                <p> Since you have viewed the solution, your score for this question will be 0. <\/p>\r\n            <\/qti-content-body><\/qti-feedback-block>\r\n            <!-- show the solution button -->\r\n            <qti-feedback-block id=\"feedbackBlock7\" identifier=\"asksolution\" outcome-identifier=\"ASKSOLUTION\" show-hide=\"show\"><qti-content-body>\r\n                <p><\/p>\r\n                <p>\r\n                    <qti-end-attempt-interaction id=\"endAttemptInteraction2\" response-identifier=\"SOLREQUEST\" title=\"Show Solution\"\/>\r\n                <\/p>\r\n            <\/qti-content-body><\/qti-feedback-block>\r\n            \r\n    <\/div> <!-- \/card-body -->\r\n    \r\n    <div class=\"qti3-player-item-card-footer qti-height-14 qti-padding-2\">\r\n      <qti-end-attempt-interaction title=\"Check Answer\" class=\"endattempt-controller-bar\" response-ident